Trigger.script:
local herb = matches[2]

local herbName = hbid.identify(herb)

echo(" [" .. herbName .. "]")

deleteLine()
echo("\n")
echo("Z:")
cecho("<yellow>\ [0]<reset>")

deleteLine()
echo("\n")
echo("S:")
cecho("<yellow>\ [0]<reset>")

deleteLine()
echo("\n")
echo("W:")
cecho("<yellow>\ [0]<reset>")

Alias.script:
local areaName = "Test_Area"


echo("1. Creating new area\n")

echo("Getting area table\n")
local t = getAreaTable(); local tr = {}; for k,v in pairs(t) do tr[v] = k end
echo("Setting new id\n")
local newid = table.maxn(tr) + 1

echo("Setting area name\n")
setAreaName( newid, areaName )
echo(string.format("Created new area %s (%d)", areaName, newid))


echo("\n\n")

echo("2. Creating first room\n")
echo("Choosing room id\n")
usableId = createRoomID() 
echo("Room id = " .. usableId .. "\n")

local x = 1
local y = 1
local z = 0

echo("Adding room\n")
addRoom(usableId)
echo("Setting coordinates\n")
setRoomCoordinates(usableId, x, y, z)
echo("Setting room area\n")
setRoomArea(usableId, 1)
echo("Centering the view\n")
centerview(usableId)

echo("\n\n")

echo("3. Setting exit stubs\n")
setExitStub(usableId, 2, true) 



echo("Test successful\n")

local roomId = __CURRENT_ROOM_ID__
if not roomExists(roomId) then 
  echo("Does not exist\n") 
  return
else
  echo("RoomId = " .. roomId .. "\n")
  local areaId = getRoomArea(roomId) 
  local areaName = getRoomAreaName(areaId)
  echo("AreaName = " .. areaName .. "\n")
end

local areaId = matches[2]
local newName = "mithlond"


echo("areaId = " .. areaId .. "\n")
echo("newName = " .. newName .. "\n")
setAreaName(areaId, newName) 
echo("Name changed\n")

local savedok = saveMap(getMudletHomeDir().."/backupMap.dat")
if not savedok then
  echo("Couldn't save :(\n")
else
  echo("Saved fine!\n")
end

local savedok = saveMap(getMudletHomeDir().."/backupMap2.dat")
if not savedok then
  echo("Couldn't save :(\n")
else
  echo("Saved fine!\n")
end

