healthSound = healthSound or {}
function healthLowSound() healthSound.checkHp() end
function healthSound.checkHp() local currentHp = tonumber(gmcp.char.state.hp)
if (currentHp < 1) then healthSound.playFile("ledwo_zywy.mp3") elseif (currentHp < 2) then healthSound.playFile("ciezko_ranny.mp3") elseif (currentHp < 3) then healthSound.playFile("w_zlej_kondycji.mp3") elseif (currentHp < 4) then healthSound.playFile("ranny.mp3") end end;
function healthSound.playFile(fileName) playSoundFile(getMudletHomeDir()..[[/Sounds/Health/]]..fileName) end

-- Copyright 2017 Filip Cyrus Bober ------------------------------------------------- -- Put your Lua functions here. -- -- -- -- Note that you can also use external Scripts -- -------------------------------------------------
healthSound = healthSound or {}
function stimpack() local currentHp = tonumber(gmcp.char.state.hp)
if (__STIMPACK_SCRIPT_ACTIVE__ == false or currentHp > 10) then return end
if __STIMPACK_ACTIVE__ == true then __STIMPACK_ACTIVE__ = false local reactivate = math.random(97, 307) tempTimer(reactivate, [[__STIMPACK_ACTIVE__ = true]])
local rnd = math.random(439, 2203)* 0.001 cecho("<yellow>Stimpack in: "..rnd.."\n")
tempTimer(rnd, [[cecho("<red>Stimpack test: ledwo zywy!\n")]]) else cecho("<yellow>Stimpack not active yet\n") end; end

