Script.script:
function scripts.ui:setup_gauge_mode0()
  setBorderBottom(0)
end

function scripts.ui:setup_gauge_mode1()
  local curr_row = scripts.ui.footer_main_labels[1]
  local curr_id = 2

  scripts.ui.gauge_back = CSSMan.new([[
    background-color: rgba(]]..scripts.ui.footer_r..[[,]]..scripts.ui.footer_g..[[,]]..scripts.ui.footer_b..[[,0);
    border-style: solid;
    border-color: white;
    border-width: 1px;
    border-radius: 5px;
    margin-right: 5px;
    margin-left: 5px;
    margin-top: 5px;
    margin-down: 5px;
    font-family:Consolas,Monaco,Lucida Console,Liberation Mono,DejaVu Sans Mono,Bitstream Vera Sans Mono,Courier New, monospace;
  ]])

  scripts.ui.gauge_front = CSSMan.new([[
    background-color: rgba(0,0,52,0);
    border-style: solid;
    border-color: white;
    border-width: 1px;
    border-radius: 5px;
    margin-right: 5px;
    margin-left: 5px;
    margin-top: 5px;
    margin-down: 5px;
    font-family:Consolas,Monaco,Lucida Console,Liberation Mono,DejaVu Sans Mono,Bitstream Vera Sans Mono,Courier New, monospace;
  ]])

  for k, v in pairs(scripts.ui.cfg["footer_items"]) do
    scripts.ui[scripts.ui["bar_to_id2"][v]] = Geyser.Gauge:new({
      name="scripts.ui."..scripts.ui["bar_to_id2"][v],
      fontSize = scripts.ui.footer_font_size,
	   width=tostring(100/scripts.ui.footer_main_items_per_row).."%",
	   h_policy=Geyser.Fixed,
    }, curr_row)

    scripts.ui[scripts.ui["bar_to_id2"][v]].front:echo([[ <font color="black">]]..scripts.ui["state_key_to_label_pre"][scripts.ui["bar_to_eng"][v]]..[[</font>]])
    scripts.ui[scripts.ui["bar_to_id2"][v]].back:echo([[ <font color="white">]]..scripts.ui["state_key_to_label_pre"][scripts.ui["bar_to_eng"][v]]..[[</font>]])
    scripts.ui[scripts.ui["bar_to_id2"][v]]:setValue(1, 1)
    scripts.ui[scripts.ui["bar_to_id2"][v]].back:setStyleSheet(scripts.ui.gauge_back:getCSS())
    scripts.ui[scripts.ui["bar_to_id2"][v]].front:setStyleSheet(scripts.ui.gauge_front:getCSS())

    if k ~= 1 and k % scripts.ui.footer_main_items_per_row == 0 then
      curr_row = scripts.ui.footer_main_labels[curr_id]
      curr_id = curr_id + 1
    end
  end
end

function scripts.ui:setup_states_window()
  scripts.ui.team_console_bar_name = "team_console"

  local start_width = 100 - scripts.ui["states_window_width"]

  scripts.ui.top_container = Geyser.Container:new({
    name = "scripts.ui.top_container",
    x=tostring(start_width).."%", y=0,
    width=tostring(scripts.ui["states_window_width"]).."%", height=tostring(scripts.ui["states_window_height"]).."%",
  })

  scripts.ui.top = Geyser.Label:new({
    name = "scripts.ui.top",
    x=0, y=0,
    width="100%", height="100%",
  }, scripts.ui.top_container)

  scripts.ui.top:setStyleSheet([[
    background-color: #181818;
    border: 1px solid grey;
  ]]) 
  color_table["team_console_bg"] = {24,24,24}
  scripts.ui.team_console_bar = Geyser.MiniConsole:new({
    name="team_console",
    x=1, y=5,
    width="98%", height="96%",
    color = "team_console_bg",
 },scripts.ui.top)

  setMiniConsoleFontSize("team_console", scripts.ui.states_font_size)
  clearUserWindow(scripts.ui.team_console_bar_name)
  scripts.ui.team_console_bar:cecho("<yellow:team_console_bg>Minikonsola, <cyan:team_console_bg>do kondycji <green:team_console_bg>zainicjowana poprawnie\n")
end

function scripts.ui:switch_states_window()
  if scripts.ui.states_window_active then
    scripts.ui.top_container:hide()
    scripts.ui.states_window_active = false
  else
    scripts.ui.states_window_active = true
    scripts.ui.top_container:show()
  end
end

function scripts.ui:setup_footer_map()
  scripts.ui.footer_map_width = scripts.ui.footer_map_width_p/100 * scripts.ui.real_footer_width
  scripts.ui.footer_map_height = scripts.ui.footer_height

  scripts.ui.footer_map_core_base_css = CSSMan.new([[
    background-color: rgba(]]..scripts.ui.footer_r..[[,]]..scripts.ui.footer_g..[[,]]..scripts.ui.footer_b..[[,0);
    font-family:Consolas,Monaco,Lucida Console,Liberation Mono,DejaVu Sans Mono,Bitstream Vera Sans Mono,Courier New, monospace;
  ]])

  amap.ui.mapper_container = Geyser.Container:new({
    name = "amap.ui.mapper_container",
    x=scripts.ui.footer_map_width_margin, y=scripts.ui.footer_map_height_margin,
    width=scripts.ui.footer_map_width-scripts.ui.footer_map_width_margin, height=scripts.ui.real_footer_height-scripts.ui.footer_map_height_margin,
  }, scripts.ui.footer_map)

