function ui_mapper_loaded_event()
  tempTimer(0.4, function () expandAlias("/ui_restart", false) end)
end

ScriptGroup.script:
mudlet = mudlet or {}; mudlet.mapper_script = true if not amap then amap = { db = {show_notes = true, show_binds = true}, ver = "2.11c", shortcuts = {}, curr = {}, prev = {}, backup_loc = {}, mode = "follow", walker = false, walker_delay = 2, walker_bind = nil, options = {["skok_x"] = 2, ["skok_y"] = 2, ["laczenie_stubow"] = 2}, walker_delta = 0.2, team_member = false, team_follow = false, locating = {}, walk_mode = 1, using_room_gps == true } end
enableTrigger("room-gps") function amap:check_room_gps_options() enableTrigger("room-gps") if amap.using_room_gps == false then disableTrigger("room-gps") end end
amap.id_to_open_gate = { [14916] = "uderz w brame", [14917] = "uderz w brame", [1707] = "uderz w brame", [1706] = "uderz w brame", [15202] = "uderz w brame", [15203] = "uderz w brame", }
amap.walk_mode_to_prefix = {"", "przemknij ", "przemknij z druzyna "}
amap.long_to_short = { ["north"] = "n", ["south"] = "s", ["east"] = "e", ["west"] = "w", ["northeast"] = "ne", ["northwest"] = "nw", ["southeast"] = "se", ["southwest"] = "sw", ["up"] = "u", ["down"] = "d" }
